function uuidv4() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    var r = Math.random() * 16 | 0, v = c == 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

const input = items[0].json.body;
const reqId = input.requestId || uuidv4();
const payload = input.payload;
const context = input.context || {};

if (!payload || !payload.message || typeof payload.message !== 'string' || payload.message.trim() === '') {
  return [{
    json: {
      httpStatus: 400,
      response: {
        status: "error",
        errorType: "invalid_payload",
        reply: "Invalid message",
        requestId: reqId
      }
    }
  }];
}

return [{
  json: {
    httpStatus: 200,
    response: {
      status: "ok",
      reply: "Echo: " + payload.message,
      requestId: reqId,
      meta: {
        handledBy: "WF-102",
        timestamp: new Date().toISOString(),
        uiMode: context.uiMode || null,
        dayPhase: context.dayPhase || null
      }
    }
  }
}];
